Scripts.OnClick:
				if (ArcanumGeneralFrame:IsVisible()) then
		            HideUIPanel(ArcanumGeneralFrame);
                    return;
                else
                    ShowUIPanel(ArcanumGeneralFrame);
                    ArcanumGeneralTab_OnClick(1);
                    return;
                end

                if ArcanumConfig.InterfaceVersion == 1 then
				    Arcanum_BuffMenu(arg1);
                else
                    if ArcanumConfig.BuffType == 0 then
                        Arcanum_BuffCast(4);
                    else
                        Arcanum_BuffCast(5);
                    end
                end

                if ArcanumConfig.InterfaceVersion == 1 then
				    Arcanum_ArmorMenu(arg1);
                else
                    if ArcanumConfig.BuffType == 0 then
                        Arcanum_ArmorCast(2);
                    else
                        Arcanum_ArmorCast(3);
                    end
                end

                if ArcanumConfig.InterfaceVersion == 1 then
				    Arcanum_MagicMenu(arg1);
                else
                    if ArcanumConfig.BuffType == 0 then
                        Arcanum_MagicCast(6);
                    else
                        Arcanum_MagicCast(7);
                    end
                end

                if ArcanumConfig.InterfaceVersion == 1 then
				    Arcanum_MountMenu(arg1);
                else
                    Arcanum_MountCast(1, arg1);
                end

            if ArcanumConfig.InterfaceVersion == 1 then
				    Arcanum_PortalMenu(arg1);
                else
                    if ArcanumConfig.BuffType == 0 then
                        Arcanum_PortalCast(14);
                    else
                        Arcanum_PortalCast(20);
                    end
                end

if ArcanumConfig.BuffType == 0 then
    Arcanum_PortalCast(15);
else
    Arcanum_PortalCast(21);
end

if ArcanumConfig.BuffType == 0 then
    Arcanum_PortalCast(16);
else
    Arcanum_PortalCast(22);
end
