Scripts.OnClick:
if (IsShiftKeyDown()) then
	TargetByName(getglobal("Asmo_ECB_"..this:GetID()).name);
elseif (IsControlKeyDown()) then
	AsmoMOD_SwiftRemove(getglobal("Asmo_ECB_"..this:GetID()).name, this:GetID(), true);
else
	AsmoMOD_ManualSwift(this:GetID());
end
