Scripts.OnClick:
--DEFAULT_CHAT_FRAME:AddMessage("Do the dew " .. this:GetName());
						local dewdrop = this.dewdrop;
						if (dewdrop) then
							local parent = this:GetParent();
							if (this.dewdrop:IsOpen(parent)) then
								dewdrop:Close(parent);
							else
								dewdrop:Open(parent);
							end
						end
						PlaySound("igMainMenuOptionCheckBoxOn");

Scripts.OnHide:
local dewdrop = this.dewdrop;
if (dewdrop) then
	local parent = this:GetParent();
	dewdrop:Close(parent);
end

Scripts.OnEnter:
if (not AutoBar.dragging and not CursorHasItem()) then
	SetCursor("CAST_CURSOR");
end
AutoBarConfig:ButtonSetTooltip();

Scripts.OnLeave:
if (not AutoBar.dragging and not CursorHasItem()) then
	SetCursor(nil);
end
GameTooltip:Hide();
