Scripts.OnClick:
CT_RaidTrackerAcceptDeleteFrame.id = this:GetParent().raidid;
CT_RaidTrackerAcceptDeleteFrame.typeid = this:GetParent().playername
CT_RaidTrackerAcceptDeleteFrame.type = "player";
CT_RaidTrackerAcceptDeleteFrame.header = "Delete Player";
CT_RaidTrackerAcceptDeleteFrame:Show();

Scripts.OnEnter:
getglobal(this:GetParent():GetName() .. "DeleteText"):SetTextColor(0.8, 0.5, 0);
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
GameTooltip:ClearLines();
GameTooltip:AddLine("Click to delete", 1, 0.7, 0);
GameTooltip:Show();
getglobal(this:GetParent():GetName() .. "MouseOver"):Show();

Scripts.OnClick:
CT_RaidTrackerEditNoteFrame.type = "the raid entry";
CT_RaidTrackerEditNoteFrame.text = this:GetParent().raidtitle;
CT_RaidTrackerEditNoteFrame.color = "ff00ff00";
CT_RaidTrackerEditNoteFrame.id = this:GetParent().raidid;
CT_RaidTrackerEditNoteFrame.playerid = this:GetParent().playername;
CT_RaidTrackerEditNoteFrame:Show();

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
GameTooltip:ClearLines();
GameTooltip:AddLine("Click to edit note", 1, 0.7, 0);
GameTooltip:AddLine(CT_RaidTracker_RaidLog[this:GetParent().raidid]["Notes"][this:GetParent().playername], 0.6, 0.6, 0.6);
GameTooltip:Show();
getglobal(this:GetParent():GetName() .. "MouseOver"):Show();

Scripts.OnLoad:
getglobal(this:GetName() .. "MouseOver"):SetAlpha(0.25);
getglobal(this:GetName() .. "MouseOver"):SetVertexColor(1, 1, 0);
getglobal(this:GetName() .. "MouseOverLeft"):SetAlpha(0.45);
getglobal(this:GetName() .. "MouseOverLeft"):SetVertexColor(1, 1, 0);
getglobal(this:GetName() .. "MouseOverRight"):SetAlpha(0.45);
getglobal(this:GetName() .. "MouseOverRight"):SetVertexColor(1, 1, 0);

Scripts.OnClick:
CT_RaidTrackerAcceptDeleteFrame.id = this:GetParent().raidid;
CT_RaidTrackerAcceptDeleteFrame.typeid = this:GetParent().itemid
CT_RaidTrackerAcceptDeleteFrame.type = "item";
CT_RaidTrackerAcceptDeleteFrame.header = "Delete Item";
CT_RaidTrackerAcceptDeleteFrame:Show();

Scripts.OnEnter:
getglobal(this:GetName() .. "Text"):SetTextColor(0.8, 0.5, 0);
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
GameTooltip:ClearLines();
GameTooltip:AddLine("Click to delete", 1, 0.7, 0);
GameTooltip:Show();

Scripts.OnClick:
CT_RaidTrackerEditNoteFrame.type = "item";
CT_RaidTrackerEditNoteFrame.text = CT_RaidTracker_RaidLog[this:GetParent().raidid]["Loot"][this:GetParent().itemid]["item"]["name"];
CT_RaidTrackerEditNoteFrame.color = CT_RaidTracker_RaidLog[this:GetParent().raidid]["Loot"][this:GetParent().itemid]["item"]["c"];
CT_RaidTrackerEditNoteFrame.id = this:GetParent().raidid;
CT_RaidTrackerEditNoteFrame.itemid = this:GetParent().itemid;
CT_RaidTrackerEditNoteFrame:Show();

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
GameTooltip:ClearLines();
GameTooltip:AddLine("Click to edit note", 1, 0.7, 0);
GameTooltip:AddLine(CT_RaidTracker_RaidLog[this:GetParent().raidid]["Loot"][this:GetParent().itemid]["note"], 0.6, 0.6, 0.6);
GameTooltip:Show();

Scripts.OnClick:
CT_RaidTrackerEditNoteFrame.text = CT_RaidTracker_GetRaidTitle(CT_RaidTrackerFrame.selected, 1);
CT_RaidTrackerEditNoteFrame.type = "raid";
CT_RaidTrackerEditNoteFrame.id = CT_RaidTrackerFrame.selected;
CT_RaidTrackerEditNoteFrame.color = "0000FF00";
CT_RaidTrackerEditNoteFrame:Show();

CT_RaidTrackerAcceptDeleteFrame.id = CT_RaidTrackerFrame.selected;
CT_RaidTrackerAcceptDeleteFrame.type = "raid";
CT_RaidTrackerAcceptDeleteFrame.header = "Delete Raid";
CT_RaidTrackerAcceptDeleteFrame:Show();

CT_RaidTracker_GetPage();
if ( CT_RaidTrackerFrame.type == "raids" or not CT_RaidTrackerFrame.type ) then
	CT_RaidTrackerFrame.type = "items";
	this:SetText("View Players");
elseif ( CT_RaidTrackerFrame.type == "items" ) then
	CT_RaidTrackerFrame.type = "raids";
	this:SetText("View Items");
elseif ( CT_RaidTrackerFrame.type == "player" ) then
	CT_RaidTrackerFrame.type = "playeritems";
	this:SetText("View Raids");
elseif ( CT_RaidTrackerFrame.type == "playeritems" ) then
	CT_RaidTrackerFrame.type = "player";
	this:SetText("View Loot");
end
CT_RaidTracker_UpdateView();

Scripts.OnShow:
if ( not this.selected ) then
	this.type = "raids";
	if ( getn(CT_RaidTracker_RaidLog) > 0 ) then
		this.selected = 1;
	end
end
CT_RaidTracker_Update();
CT_RaidTracker_UpdateView();

getglobal(this:GetName() .. "HeaderText"):SetText("Are you sure you want to delete this " .. this.type .. "?");
getglobal(this:GetName() .. "HeaderText"):SetText(this.header);
getglobal(this:GetName() .. "Info"):SetText("Are you sure you want to delete this " .. this.type .. "?");
PlaySound("UChatScrollButton");

Scripts.OnMouseDown:
if ( arg1 == "LeftButton" ) then
	this:GetParent():StartMoving();
elseif ( arg1 == "RightButton" ) then
	this:GetParent():ClearAllPoints();
	this:GetParent():SetPoint("CENTER", "UIParent", "CENTER");
end
