CHH_HintOptionSetConfigPrepand( 'raid_class', 'needyListHealShow' );
CHH_HintOptionSetConfigPrepand( 'needy_list', 'needyListHeal' );

getglobal(this:GetName()..'FrameHeightLow'):SetText( '32' );
