Scripts.OnClick:
PlaySound("igMainMenuOpen");
PanelTemplates_Tab_OnClick(ER_OptionsSideFrame);

if ( ER_OptionsSideFrame.selectedTab == 1 ) then
	ER_OptionsSideFrameRaidPanel:Show();
	ER_OptionsSideFrameBuffsPanel:Hide();
	ER_OptionsSideFrameMainTanksPanel:Hide();
	ER_OptionsSideFrameTargetsPanel:Hide();
	ER_OptionsSideFrameMiscPanel:Hide();
elseif ( ER_OptionsSideFrame.selectedTab == 2 ) then
	ER_OptionsSideFrameRaidPanel:Hide();
	ER_OptionsSideFrameBuffsPanel:Show();
	ER_OptionsSideFrameMainTanksPanel:Hide();
	ER_OptionsSideFrameTargetsPanel:Hide();
	ER_OptionsSideFrameMiscPanel:Hide();
elseif ( ER_OptionsSideFrame.selectedTab == 3 ) then
	ER_OptionsSideFrameRaidPanel:Hide();
	ER_OptionsSideFrameBuffsPanel:Hide();
	ER_OptionsSideFrameMainTanksPanel:Show();
	ER_OptionsSideFrameTargetsPanel:Hide();
	ER_OptionsSideFrameMiscPanel:Hide();
elseif ( ER_OptionsSideFrame.selectedTab == 4 ) then
	ER_OptionsSideFrameRaidPanel:Hide();
	ER_OptionsSideFrameBuffsPanel:Hide();
	ER_OptionsSideFrameMainTanksPanel:Hide();
	ER_OptionsSideFrameTargetsPanel:Show();
	ER_OptionsSideFrameMiscPanel:Hide();
elseif ( ER_OptionsSideFrame.selectedTab == 5 ) then
	ER_OptionsSideFrameRaidPanel:Hide();
	ER_OptionsSideFrameBuffsPanel:Hide();
	ER_OptionsSideFrameMainTanksPanel:Hide();
	ER_OptionsSideFrameTargetsPanel:Hide();
	ER_OptionsSideFrameMiscPanel:Show();
end

local id = this:GetParent():GetID();
local tempClass = ER_Config.classes[id - 1];
ER_Config.classes[id - 1] = ER_Config.classes[id];
ER_Config.classes[id] = tempClass;
ER_OptionsSideFrame_Update();
ER_RaidPulloutFrame_Refresh();

local id = this:GetParent():GetID();
local tempClass = ER_Config.classes[id + 1];
ER_Config.classes[id + 1] = ER_Config.classes[id];
ER_Config.classes[id] = tempClass;
ER_OptionsSideFrame_Update();
ER_RaidPulloutFrame_Refresh();

Scripts.OnShow:
UIDropDownMenu_Initialize(this, ER_AlignmentDropDownMenu_Initialize);
UIDropDownMenu_SetSelectedValue(this, ER_Config.alignment);
UIDropDownMenu_SetWidth(100, this);

Scripts.OnClick:
if ( this:GetChecked() == 1 ) then
	SHOW_CASTABLE_BUFFS = "1";
else
	SHOW_CASTABLE_BUFFS = "0";
end
ER_OptionsSideFrame_Update();

if ( this:GetChecked() == 1 ) then
	SHOW_DISPELLABLE_DEBUFFS = "1";
else
	SHOW_DISPELLABLE_DEBUFFS = "0";
end
ER_OptionsSideFrame_Update();

Scripts.OnShow:
UIDropDownMenu_Initialize(this, ER_AlignmentDropDownMenu_Initialize);
UIDropDownMenu_SetSelectedValue(this, ER_Config.mainTanksAlignment);
UIDropDownMenu_SetWidth(100, this);

Scripts.OnValueChanged:
if ( this:GetValue() ~= ER_Config.mainTanksScale ) then
	local previousScale = ER_Config.mainTanksScale;
	ER_Config.mainTanksScale = this:GetValue();
	getglobal(this:GetName().."Text"):SetText(ER_SCALE.." "..ER_Config.mainTanksScale.."%");
	local point, relativeObject, relativePoint, x, y = ER_MainTanksPlaceHolder:GetPoint(1);
	ER_MainTanksPlaceHolder_UpdateScale();
	local deltaScale = previousScale / ER_Config.mainTanksScale;
	ER_MainTanksPlaceHolder:SetPoint(point, relativeObject, relativePoint, x*deltaScale, y*deltaScale);
	ER_MainTanksPlaceHolder_SaveLocation();
	ER_UPDATE_INTERVAL = 0.2;
end

Scripts.OnShow:
UIDropDownMenu_Initialize(this, ER_AlignmentDropDownMenu_Initialize);
UIDropDownMenu_SetSelectedValue(this, ER_Config.targetsAlignment);
UIDropDownMenu_SetWidth(100, this);

Scripts.OnValueChanged:
if ( this:GetValue() ~= ER_Config.targetsScale ) then
	local previousScale = ER_Config.targetsScale;
	ER_Config.targetsScale = this:GetValue();
	getglobal(this:GetName().."Text"):SetText(ER_SCALE.." "..ER_Config.targetsScale.."%");
	local point, relativeObject, relativePoint, x, y = ER_TargetsFrame:GetPoint(1);
	ER_TargetsFrame_UpdateScale();
	local deltaScale = previousScale / ER_Config.targetsScale;
	ER_TargetsFrame:SetPoint(point, relativeObject, relativePoint, x*deltaScale, y*deltaScale);
	ER_TargetsFrame_SaveLocation();
	if ( ER_Config.mainTanksAreAttached and ER_Config.mainTanksAttachment == "TARGETS" ) then
		ER_RaidPulloutFrame_Update();
	end
end

Scripts.OnLoad:
this:RegisterForDrag("LeftButton");
PanelTemplates_SetNumTabs(this, 5);
this.selectedTab = 1;
PanelTemplates_UpdateTabs(this);
ER_RaidInfoFrame = { };
ER_RaidInfoFrame.point, ER_RaidInfoFrame.relativeTo, ER_RaidInfoFrame.relativePoint,
	ER_RaidInfoFrame.offsetX, ER_RaidInfoFrame.offsetY = RaidInfoFrame:GetPoint();

Scripts.OnShow:
ER_OptionsSideFrame_Update();
UIFrameFadeIn(ER_ContainerFrame, 1, 0, ER_CONTAINER_FRAME_ALPHA);
ER_RaidFrameOptionsButton_Update();
RaidInfoFrame:SetPoint("TOPLEFT", ER_OptionsSideFrame, "TOPRIGHT", -1, -18);

Scripts.OnHide:
ER_ContainerFrame:Hide();
ER_RaidFrameOptionsButton_Update();
RaidInfoFrame:SetPoint(ER_RaidInfoFrame.point, ER_RaidInfoFrame.relativeTo, ER_RaidInfoFrame.relativePoint,
	ER_RaidInfoFrame.offsetX, ER_RaidInfoFrame.offsetY);

Scripts.OnClick:
ER_Config["optionsSideIsShown"] = not ER_Config["optionsSideIsShown"];
if ( ER_OptionsSideFrame:IsShown() ) then
	ER_OptionsSideFrame:Hide();
else
	ER_OptionsSideFrame:Show();
end
