Scripts.OnEvent:

               if (event == "CHAT_MSG_WHISPER") then
	       		FTL_ProcessChat(arg1, arg2);
	       	elseif (event == "UI_ERROR_MESSAGE") then
	       		FTL_ProcessError(arg1);
	       	elseif (event == "AUTOFOLLOW_BEGIN") then
	       		FTL_Following(arg1);
	       	elseif (event == "AUTOFOLLOW_END") then
	       		FTL_Stopped();
		end
