Scripts.OnEnter:
local iconNum = 1

if GR_TT_targetWho[iconNum][1] ~= "none" then
local temp_string = "|cFFc0c0FFTargetters: |r\n"
for key, value in GR_TT_targetWho[iconNum] do
temp_string = temp_string..value.."\n"
end

local tt = getglobal("GameTooltip");
tt:SetOwner(this, "ANCHOR_CURSOR");
tt:SetText(temp_string);
end

