Scripts.OnClick:
if ( this.sortType ) then
	GuildAdsTrade.sortTrade(this.sortType);
end
PlaySound("igMainMenuOptionCheckBoxOn");

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
if GuildAdsTrade.currentItem and GuildAdsTrade.currentItem~="" then
	GameTooltip:SetHyperlink(GuildAdsTrade.currentItem);
	CursorUpdate();
else
	GameTooltip:SetText(GUILDADS_TRADE_SHIFTCLICKHELP);
end

Scripts.OnMouseUp:
if ( this:GetParent():GetParent().isMoving ) then
	this:GetParent():GetParent():StopMovingOrSizing();
	this:GetParent():GetParent().isMoving = false;
end

Scripts.OnMouseDown:
if ( ( ( not this:GetParent():GetParent().isLocked ) or ( this:GetParent():GetParent().isLocked == 0 ) ) and ( arg1 == "LeftButton" ) ) then
	this:GetParent():GetParent():StartMoving();
	this:GetParent():GetParent().isMoving = true;
end

Scripts.OnHide:
if ( this:GetParent():GetParent().isMoving ) then
	this:GetParent():GetParent():StopMovingOrSizing();
	this:GetParent():GetParent().isMoving = false;
end
