Scripts.OnMouseUp:
if ( this.isMoving ) then
	this:StopMovingOrSizing();
	this.isMoving	=	false;
end

Scripts.OnMouseDown:
if ( ( ( not this.isLocked ) or	(	this.isLocked	== 0 ) ) and ( arg1	== "LeftButton"	)	)	then
	this:StartMoving();
	this.isMoving	=	true;
end

Scripts.OnClick:
MG_UpdateInviteList();
MG_BroadcastRaidInfo();
this:Hide();
MG_SetRaid_Save:Hide();
MG_Invite_Stop:Show();
MG_ToggleRemoteButton:Show();

MG_StopBroadcast();
this:Hide();
MG_Invite_Start:Show();
MG_SetRaid_Save:Show();
MG_ToggleRemoteButton:Hide();
if(MG_RaidRemote:IsVisible()) then
	MG_ToggleRemote();
end

Scripts.OnMouseUp:
if (this.isMoving) then
	this:StopMovingOrSizing();
	this.isMoving	=	false;
end

Scripts.OnMouseDown:
if(arg1	== "LeftButton" and MG_DKPDetached == 1) then
	this:StartMoving();
	this.isMoving	=	true;
end

Scripts.OnMouseUp:
if ( this.isMoving ) then
	this:StopMovingOrSizing();
	this.isMoving	=	false;
end

Scripts.OnMouseDown:
if(arg1	== "LeftButton") then
	this:StartMoving();
	this.isMoving	=	true;
end

Scripts.OnMouseUp:
if ( this.isMoving ) then
	this:StopMovingOrSizing();
	this.isMoving	=	false;
end

Scripts.OnMouseDown:
if(arg1	== "LeftButton") then
	this:StartMoving();
	this.isMoving	=	true;
end
