Scripts.OnClick:
PanelTemplates_SetTab(MetaHudOptionsFrame, this:GetID());
for i = 1, MetaHudO_NUMTABS do
	if i == this:GetID() then
		getglobal(this:GetName().."Text"):Hide();
		getglobal(this:GetName().."HighlightText"):Hide();
		getglobal(this:GetName().."DisabledText"):Show();
		getglobal("MetaHud_Tab"..i):Show();
	else
		getglobal("MetaHudOptionsFrameTab"..i.."Text"):Show();
		getglobal("MetaHudOptionsFrameTab"..i.."HighlightText"):Hide();
		getglobal("MetaHudOptionsFrameTab"..i.."DisabledText"):Hide();
		getglobal("MetaHud_Tab"..i):Hide();
	end
end

Scripts.OnLoad:
local parent = this:GetParent();
this.setting_name = string.gsub( parent:GetName() , "MetaHud_Pos_", "");
this:RegisterForClicks("LeftButtonUp", "RightButtonUp");

Scripts.OnClick:
if(arg1 == "RightButton") then
	MetaHud:SetConfig(this.setting_name, MetaHud:GetConfig(this.setting_name) - 10);
else
	MetaHud:SetConfig(this.setting_name, MetaHud:GetConfig(this.setting_name) - 1);
end
local parent = this:GetParent();
local text = getglobal(parent:GetName().."_Minitext");
text:SetText( MetaHud:GetConfig(this.setting_name) );
MetaHud:init();

Scripts.OnLoad:
local parent = this:GetParent();
this.setting_name = string.gsub( parent:GetName() , "MetaHud_Pos_", "");
this:RegisterForClicks("LeftButtonUp", "RightButtonUp");

Scripts.OnClick:
if (arg1 == "RightButton") then
    MetaHud:SetConfig(this.setting_name, MetaHud:GetConfig(this.setting_name) + 10);
else
    MetaHud:SetConfig(this.setting_name, MetaHud:GetConfig(this.setting_name) + 1);
end
local parent = this:GetParent();
local text = getglobal(parent:GetName().."_Minitext");
text:SetText( MetaHud:GetConfig(this.setting_name) );
MetaHud:init();

Scripts.OnLoad:
local parent = this:GetParent();
this.setting_name = string.gsub( parent:GetName() , "MetaHud_Pos_", "");
getglobal( this:GetName().."Text"):SetText( " "..(MetaHudO_locale[this.setting_name] or "No Locale") );
this:SetText( tonumber(MetaHud:GetConfig(this.setting_name)) or 0 );
this.oldvalue = this:GetText();

Scripts.OnTextChanged:
if this:GetNumber() == this:GetText() then
    this.oldvalue = this:GetText();
end
this:SetText( this:GetNumber() or 0 );
MetaHud:SetConfig( this.setting_name, this:GetText() );

Scripts.OnEnter:
if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end

Scripts.OnTextChanged:
local scrollBar = getglobal(this:GetParent():GetName().."ScrollBar")
this:GetParent():UpdateScrollChildRect();
local min, max = scrollBar:GetMinMaxValues();
if ( max > 0 and (this.max ~= max) ) then
    this.max = max;
    scrollBar:SetValue(max);
end

Scripts.OnEditFocusLost:

local name = string.gsub( this:GetParent():GetParent():GetName() , "MetaHud_Edit_", "");
local dropdown = getglobal("MetaHud_Edit_"..name.."_Selection");
MetaHud:SetConfig( name , this:GetText() );

UIDropDownMenu_SetSelectedID( dropdown, 1 );
UIDropDownMenu_SetText( MetaHudO_SELECTION[name]["table"][1] , dropdown);

for key, str in pairs(MetaHudO_SELECTION[name]["values"]) do
    if MetaHud:GetConfig(name) == str then
        UIDropDownMenu_SetSelectedID( dropdown, key );
        droptext = MetaHudO_SELECTION[name]["table"][key];
        UIDropDownMenu_SetText(droptext, dropdown);
    end
end

MetaHud:init();
MetaHudO_updateTexts(name);
MetaHudHelpFrame:Hide();

Scripts.OnShow:
local droptext;
local dropdown = getglobal(this:GetName().."_Selection");

local label = getglobal(this:GetName().."_SelectionLabel");
label:SetText(MetaHudO_locale[this.setting_name] or "no Locale");

local box = getglobal(this:GetName().."ScrollFrameText");
box:SetText( MetaHud:GetConfig(this.setting_name) );

local tipptext = MetaHudO_SELECTION[this.setting_name]["ttt"];
dropdown.tooltipText = tipptext;

UIDropDownMenu_SetSelectedID( dropdown, 1 );
UIDropDownMenu_SetText( MetaHudO_SELECTION[this.setting_name]["table"][1] , dropdown);

for key, str in pairs(MetaHudO_SELECTION[this.setting_name]["values"]) do
    if MetaHud:GetConfig(this.setting_name) == str then
        UIDropDownMenu_SetSelectedID( dropdown, key );
        droptext = MetaHudO_SELECTION[this.setting_name]["table"][key];
        UIDropDownMenu_SetText(droptext, dropdown);
    end
end
