Scripts.OnClick:
if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
MiniGroup2:MGOptionsCheckButtonOnClick();

Scripts.OnEnter:
if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end

if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end

Scripts.OnValueChanged:
MiniGroup2.SetOpt("OORSpell",this:GetValue());
GameTooltip:SetOwner(GameTooltip, "ANCHOR_NONE")
GameTooltip:SetAction(this:GetValue());
local spname = GameTooltipTextLeft1:GetText();
if (spname ~= nil) then
	if (string.len(spname) > 12) then spname = string.sub(spname,0,12).."..."; end
	getglobal(this:GetName().."Text"):SetText(MG_OPTIONS_OORSELECT_TEXT..": "..spname);
else
	getglobal(this:GetName().."Text"):SetText(MG_OPTIONS_OORSELECT_TEXT..": None");
end
GameTooltip:Hide();

Scripts.OnMouseUp:
MGOptionsFrame_CastBarColorSelect:Hide();
MGOptionsFrame_CastBarColor:EnableMouse(true);
local castr,castg,castb = MGOptionsFrame_CastBarColorSelect:GetColorRGB();
MGOptionsFrame_CastBarColorSwatchBg:SetTexture(castr,castg,castb);
MiniGroup2.SetOpt("CastBarR",castr);
MiniGroup2.SetOpt("CastBarG",castg);
MiniGroup2.SetOpt("CastBarB",castb);

Scripts.OnHide:
if ( ColorPickerFrame:IsVisible() ) then
	ColorPickerFrame:Hide();
end
if (MYADDONS_ACTIVE_OPTIONSFRAME == this) then
	ShowUIPanel(myAddOnsFrame);
end
