Scripts.OnEnter:
local index = this:GetID() + FauxScrollFrame_GetOffset(MI2B_ListScrollFrame);
local mobDataB = MI2BrowseTable[index];
MI2B_CreateBrowseTooltip(mobDataB, GameTooltip);

if this.tooltiptext then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltiptext);
	GameTooltip:Show();
end

Scripts.OnShow:
local text = getglobal(this:GetName().."Text");
text:SetText(MI2B_PERCHAR);
text:SetTextColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
--local checked = 0;
--local save = getglobal(MI2BSave);
--if (MI2BSave and MI2BSave.perchar and MI2BSave.perchar == 1)
--	checked = 1;
--end

OptionsFrame_EnableCheckBox(this);
this:SetChecked(MI2BSave.perchar);
this.tooltipText = MI2B_PERCHAR_TOOLTIP;

Scripts.OnClick:
if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
	MI2BSave.perchar = 1;
else
	PlaySound("igMainMenuOptionCheckBoxOn");
	MI2BSave.perchar = nil;
end

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	--MI2B_BrowseMinLevel:SetFocus();
else
	MI2B_BrowseMinLevel:SetFocus();
end

if ( IsShiftKeyDown() ) then
	MI2B_BrowseName:SetFocus();
else
	MI2B_BrowseMaxLevel:SetFocus();
end

if ( IsShiftKeyDown() ) then
	MI2B_BrowseMinLevel:SetFocus();
else
	MI2B_BrowseXP:SetFocus();
end

if ( IsShiftKeyDown() ) then
	MI2B_BrowseMaxLevel:SetFocus();
else
	MI2B_BrowseKills:SetFocus();
end

if ( IsShiftKeyDown() ) then
	MI2B_BrowseXP:SetFocus();
else
	MI2B_BrowseMobValueG:SetFocus();
end

if ( IsShiftKeyDown() ) then
	MI2B_BrowseKills:SetFocus();
else
	MI2B_BrowseMobValueS:SetFocus();
end

if ( IsShiftKeyDown() ) then
	MI2B_BrowseMobValueG:SetFocus();
else
	MI2B_BrowseMobValueC:SetFocus();
end

if ( IsShiftKeyDown() ) then
	MI2B_BrowseMobValueS:SetFocus();
else
	MI2B_BrowseItemName:SetFocus();
end

if ( IsShiftKeyDown() ) then
	MI2B_BrowseMobValueC:SetFocus();
else
	MI2B_BrowseLocation:SetFocus();
end

if ( IsShiftKeyDown() ) then
	MI2B_BrowseItemName:SetFocus();
else
	MI2B_BrowseName:SetFocus();
end

Scripts.OnClick:

if (MI2B_BrowseFrame:IsVisible()) then
	MI2B_BrowseFrame:Hide();
else
	MI2B_RefreshBrowseList();
end
