Bindings.Binding:
if ( OneHitWonder_Enabled == 1 ) then
	OneHitWonder_Enabled = 0;
	OneHitWonder_Print(ONEHITWONDER_CHAT_DISABLED);
else
	OneHitWonder_Enabled = 1;
	OneHitWonder_Print(ONEHITWONDER_CHAT_ENABLED);
end
OneHitWonder_SetOptions();

if ( OneHitWonder_AutomatedGiveMeWonder == 1 ) then
	OneHitWonder_AutomatedGiveMeWonder = 0;
	OneHitWonder_Print(ONEHITWONDER_WONDER_DISABLED);
else
	OneHitWonder_Enabled = 1;
	OneHitWonder_AutomatedGiveMeWonder = 1;
	OneHitWonder_Print(ONEHITWONDER_WONDER_ENABLED);
end
OneHitWonder_SetOptions();

local value = 0;
if ( OneHitWonder_Priest_AutoBuffInnerFire == 0 ) then
	value = 1;
end
OneHitWonder_SetAutoBuffInnerFire(value);
local mode = ONEHITWONDER_TOGGLE_DISABLED_CHAT;
if ( value == 1 ) then
	mode = ONEHITWONDER_TOGGLE_ENABLED_CHAT;
end
OneHitWonder_Print(format(ONEHITWONDER_TOGGLE_INNERFIRE_CHAT, mode));
OneHitWonder_SetOptions();
