Scripts.OnClick:
OneRaid_Options.disableStickyOfficer = this:GetChecked();
if (OneRaid_Options.disableStickyOfficer) then
ChatTypeInfo["OFFICER"].sticky = nil;
else
ChatTypeInfo["OFFICER"].sticky = 1;
end

OneRaid_Options.disableStickyChannel = this:GetChecked();
if (OneRaid_Options.disableStickyChannel) then
ChatTypeInfo["CHANNEL"].sticky = nil;
else
ChatTypeInfo["CHANNEL"].sticky = 1;
end

OneRaid_Options.showAllGroupWindows = this:GetChecked();
for k, v in pairs(OneRaid.Group.frames) do
	if (not v.inactive) then
		if (OneRaid_Options.Groups[v.name].buffMonitor or OneRaid_Options.Groups[v.name].debuffMonitor) then
			for i, j in pairs(v.frames) do
				OneRaid.Group:UpdateBuffs(v, j.unit);
			end
		end
		if (OneRaid_Options.Groups[v.name].tankMonitor) then
			OneRaid.Group:UpdateFrame(v, 1);
		else
			OneRaid.Group:Filter(v);
			OneRaid.Group:UpdateFrame(v);
		end
	end
end

OneRaid_Options.unlockMessageFrame = this:GetChecked();
if (OneRaid_Options.unlockMessageFrame) then
OneRaid_Message_Frame_BackDrop:Show();
else
OneRaid_Message_Frame_BackDrop:Hide();
end

Scripts.OnLoad:
this:SetMinMaxValues(0, 360);
getglobal(this:GetName() .. "Text"):SetText(ONERAID_ICON_PLACEMENT);
getglobal(this:GetName() .. "Text"):SetTextColor(1, 1, 1);
getglobal(this:GetName() .. "Low"):SetText("0");
getglobal(this:GetName() .. "High"):SetText("360");
this:SetValueStep(1);
this:SetValue(0);

Scripts.OnValueChanged:
if (OneRaid_Options) then
OneRaid_Options.iconAngle = tonumber(this:GetValue());
local angle = OneRaid_Options.iconAngle or 0;
OneRaid_Icon:ClearAllPoints();
OneRaid_Icon:SetPoint("CENTER", "Minimap", "CENTER", (80 * cos(angle)) - 1, (80 * sin(angle)) - 1);
end
