Scripts.OnLoad:
this:SetMinMaxValues(0, 1);
getglobal(this:GetName() .. "Text"):SetText(ONERAID_STATUSBAR_ALPHA);
getglobal(this:GetName() .. "Text"):SetTextColor(1, 1, 1);
getglobal(this:GetName() .. "Low"):SetText("0");
getglobal(this:GetName() .. "High"):SetText("1");
this:SetValueStep(.01);
this:SetValue(1);

this:SetMinMaxValues(0, 1);
getglobal(this:GetName() .. "Text"):SetText(ONERAID_DEBUFF_ALPHA);
getglobal(this:GetName() .. "Text"):SetTextColor(1, 1, 1);
getglobal(this:GetName() .. "Low"):SetText("0");
getglobal(this:GetName() .. "High"):SetText("1");
getglobal(this:GetName() .. "Low"):SetText("0");
this:SetValueStep(.01);
this:SetValue(1);
