Scripts.OnClick:
if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
SCT:OptionsCheckButtonOnClick();

Scripts.OnEnter:
if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end

if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end

if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end

if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end

Scripts.OnClick:
if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
SCT:OptionsCheckButtonOnClick();
SCT:ChangeStickyMode(this:GetChecked());

if ( ColorPickerFrame:IsVisible() ) then
	ColorPickerFrame:Hide();
end

SCT:AniInit();
SCT:HideMenu();

Scripts.OnLoad:
StaticPopupDialogs["SCT_RESET"] = {
  text = SCT.LOCALS.OPTION_MISC9.tooltipText,
  button1 = TEXT(OKAY) ,
  button2 = TEXT(CANCEL) ,
  OnAccept = function()
      SCT:Reset();
  end,
  timeout = 0,
  whileDead = 1,
  hideOnEscape = 1,
  showAlert = 1
};

Scripts.OnClick:
if ( this:GetChecked() ) then
	SCT:ToggleActive(true);
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	SCT:ToggleActive(false);
	PlaySound("igMainMenuOptionCheckBoxOn");
end
SCT:OptionsCheckButtonOnClick();

if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
SCT:OptionsCheckButtonOnClick();
SCT:ChangeLightMode(this:GetChecked(), true);
