Scripts.OnShow:
if SheepModOptions[Realm][Player].Enabled then
	getglobal(this:GetName().."Text"):SetTextColor(0, 1, 0);
else
	getglobal(this:GetName().."Text"):SetTextColor(1, 0, 0);
end
SheepMod.InitMainCheckButton();

Scripts.OnClick:
SheepMod.ClickMainCheckButton();
if SheepModOptions[Realm][Player].Enabled then
	getglobal(this:GetName().."Text"):SetTextColor(0, 1, 0);
else
	getglobal(this:GetName().."Text"):SetTextColor(1, 0, 0);
end

Scripts.OnShow:
if SheepModOptions[Realm][Player].MCed == 2 then
	getglobal(this:GetName().."Text"):SetText(format(sm_button_mcandname, SheepModOptions[Realm][Player].MCName));
else
	getglobal(this:GetName().."Text"):SetText(sm_button_mc);
end
if SheepModOptions[Realm][Player].MCed == false then
	SM_SetMCName:SetText(sm_string_set_player)
end
SheepMod.InitMainCheckButton();

Scripts.OnMouseDown:
if arg1 == "RightButton" then
	TargetByName(SheepModOptions.Temp.SheepName);
else
	this:GetParent():StartMoving();
end
