Scripts.OnEnter:
if ( this:GetParent().testtooltip ) then
	GameTooltip:SetOwner(this, "ANCHOR_TOPRIGHT");
	GameTooltip:SetText(this:GetParent().testtooltip, nil, nil, nil, nil, 1);
end

if ( this.tooltip ) then
	GameTooltip:SetOwner(this, "ANCHOR_TOPLEFT");
	GameTooltip:SetText(this.tooltip, nil, nil, nil, nil, 1);
end

if ( (STranq_Saved["LockRot"] and not STranqTimers:IsVisible()) or (STranq_Timers.resizing and STranq_Timers.resizing ~= this) ) then
	STranq_TimersResizeHighlight:Hide();
else
	STranq_TimersResizeHighlight:Show();
end

if ( (STranq_Saved["LockRot"] and not STranqTimers:IsVisible()) or (STranq_Timers.resizing and STranq_Timers.resizing ~= this) ) then
	STranq_TimersGroupHighlight:Hide();
else
	if ( not STranq_Timers.resizing ) then
		STranqToolTip:SetOwner(Stranq_FrenzyTimerButton, "ANCHOR_TOPLEFT");
		STranqToolTip:SetText(STRANQ_ROTATION_GROUP, 1, 1, 1, 1);
		STranqToolTip:ClearAllPoints();
		STranqToolTip:SetPoint("BOTTOM", Stranq_FrenzyTimerButton, "TOP");
	end
	STranq_TimersGroupHighlight:Show();
end

if ( (STranq_Saved["LockRot"] and not STranqTimers:IsVisible()) or (STranq_Timers.resizing and STranq_Timers.resizing ~= this) ) then
	STranq_TimersBackupHighlight1:Hide();
	STranq_TimersBackupHighlight2:Hide();
	STranq_TimersBackupHighlight3:Hide();
else
	if ( not STranq_Timers.resizing ) then
		STranqToolTip:SetOwner(Stranq_FrenzyTimerButton, "ANCHOR_TOPLEFT");
		STranqToolTip:SetText(STRANQ_ROTATION_BACKUP, 1, 1, 1, 1);
		STranqToolTip:ClearAllPoints();
		STranqToolTip:SetPoint("BOTTOM", Stranq_FrenzyTimerButton, "TOP");
	end
	STranq_TimersBackupHighlight1:Show();
	STranq_TimersBackupHighlight2:Show();
	STranq_TimersBackupHighlight3:Show();
end

if ( (STranq_Saved["LockRot"] and not STranqTimers:IsVisible()) or (STranq_Timers.resizing and STranq_Timers.resizing ~= this) ) then
	STranq_TimersGroupsHighlight:Hide();
else
	if ( not STranq_Timers.resizing ) then
		STranqToolTip:SetOwner(Stranq_FrenzyTimerButton, "ANCHOR_TOPLEFT");
		STranqToolTip:SetText(STRANQ_ROTATION_GROUPS, 1, 1, 1, 1);
		STranqToolTip:ClearAllPoints();
		STranqToolTip:SetPoint("BOTTOM", Stranq_FrenzyTimerButton, "TOP");
	end
	STranq_TimersGroupsHighlight:Show();
end

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	STranq_Options_FailMsg:SetFocus();
else
	STranq_Options_MissMsg:SetFocus();
end

Scripts.OnLoad:
this.tooltip = STRANQ_CASTMSG_TIP;
this.testtooltip = STRANQ_CASTMSGTEST_TIP;
this.testfunc = STranq_Options_Test_Msg;

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	STranq_Options_CastMsg:SetFocus();
else
	STranq_Options_FailMsg:SetFocus();
end

Scripts.OnLoad:
this.tooltip = STRANQ_MISSMSG_TIP;
this.testtooltip = STRANQ_MISSMSGTEST_TIP;
this.testfunc = STranq_Options_Test_Msg;

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	STranq_Options_MissMsg:SetFocus();
else
	STranq_Options_CastMsg:SetFocus();
end

Scripts.OnLoad:
this.tooltip = STRANQ_FAILMSG_TIP;
this.testtooltip = STRANQ_FAILMSGTEST_TIP;
this.testfunc = STranq_Options_Test_Msg;

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	STranq_Options_RaidFailErr:SetFocus();
else
	STranq_Options_RaidErr:SetFocus();
end

Scripts.OnLoad:
this.tooltip = STRANQ_MISSERR_TIP;
this.testtooltip = STRANQ_MISSERRTEST_TIP;
this.testfunc = STranq_Options_Test_SelfErr;

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	STranq_Options_MissErr:SetFocus();
else
	STranq_Options_FailErr:SetFocus();
end

Scripts.OnLoad:
this.tooltip = STRANQ_RAIDERR_TIP;
this.testtooltip = STRANQ_RAIDERRTEST_TIP;
this.testfunc = STranq_Options_Test_RaidErr;

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	STranq_Options_RaidErr:SetFocus();
else
	STranq_Options_RaidFailErr:SetFocus();
end

Scripts.OnLoad:
this.tooltip = STRANQ_FAILERR_TIP;
this.testtooltip = STRANQ_FAILERRTEST_TIP;
this.testfunc = STranq_Options_Test_SelfErr;

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	STranq_Options_FailErr:SetFocus();
else
	STranq_Options_MissErr:SetFocus();
end

Scripts.OnLoad:
this.tooltip = STRANQ_RAIDFAILERR_TIP;
this.testtooltip = STRANQ_RAIDFAILERRTEST_TIP;
this.testfunc = STranq_Options_Test_RaidErr;

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_TOPRIGHT");
GameTooltip:SetText(this.tooltip, nil, nil, nil, nil, 1);
getglobal(this:GetName().."BG"):SetVertexColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);

GameTooltip:SetOwner(this, "ANCHOR_TOPRIGHT");
GameTooltip:SetText(this.tooltip, nil, nil, nil, nil, 1);
getglobal(this:GetName().."BG"):SetVertexColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);

GameTooltip:SetOwner(this,"ANCHOR_TOPLEFT");
GameTooltip:SetText(STRANQ_BARALPHA_TIP, nil, nil, nil, nil, 1);
GameTooltip:Show();

GameTooltip:SetOwner(this,"ANCHOR_TOPLEFT");
GameTooltip:SetText(STRANQ_BARSCALE_TIP, nil, nil, nil, nil, 1);
GameTooltip:Show();

Scripts.OnClick:
if ( IsShiftKeyDown() ) then
	StaticPopup_Show("STRANQ_DEFAULTSALL");
else
	StaticPopup_Show("STRANQ_DEFAULTS");
end
