Scripts.OnClick:
if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
end 
SmartPetEnable_Toggle();

if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
	SmartPetOptionButton:Show(); 
	SmartPet_Config.Icon = true; 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
	SmartPetOptionButton:Hide(); 
	SmartPet_Config.Icon = false; 
end

if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
end 
SmartPet_OnSlashCommand(SMARTPET_TOOLTIPS);

if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
	SmartPet_Config.Alert = true; 
	SmartPet_AttackAlertFrame:Show(); 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_ATTACKALERT_ENABLED); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
	SmartPet_Config.Alert = false; 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_ATTACKALERT_DISABLED); 	
	SmartPet_AttackAlertFrame:Hide(); 
end

if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
end 
SmartPet_OnSlashCommand(SMARTPET_SMARTFOCUS);

if (this:GetChecked()) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
	SmartPet_Config.RushAttack = true; 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_ATTACKRUN_ENABLED); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
	SmartPet_Config.RushAttack = false; 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_ATTACKRUN_DISABLED); 
end

if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
end 
SmartPet_OnSlashCommand(SMARTPET_NOCHASE);

if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
	SmartPet_Config.RecallWarn = true; 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_AUTORECALL_ENABLED); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
	SmartPet_Config.RecallWarn = false; 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_AUTORECALL_DISABLED); 
end

if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
end 
SmartPet_OnSlashCommand(SMARTPET_AUTOCOWER);

if (this:GetChecked()) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
	SmartPet_Config.SpellAttack = true; 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_SPELLATTACK_ENABLED); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
	SmartPet_Config.SpellAttack = false; 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_SPELLATTACK_DISABLED); 
end

if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
	SmartPet_Config.Scatter = true; 
	SmartPet_ScatterOptions:Show(); 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_SCATTERSHOT_ENABLED); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
	SmartPet_Config.Scatter = false; 
	SmartPet_ScatterOptions:Hide(); 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_SCATTERSHOT_DISABLED); 
end

if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
	SmartPet_HealthWarningChannels:Show(); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
	SmartPet_HealthWarningChannels:Hide(); 
end 
SmartPet_Autowarn_Toggle();

if (this:GetChecked()) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
	SmartPet_Config.DebufCheck = true; 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_DEBUFFCHECK_ENABLED); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
	SmartPet_Config.DebufCheck = false; 
	SmartPet_AddInfoMessage(SMARTPET_COMMANDS_DEBUFFCHECK_DISABLED); 
end

if (this:GetChecked()) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
end
SmartPet_ToggleAutoFeed();

if ( this:GetChecked() ) then 
	PlaySound("igMainMenuOptionCheckBoxOff"); 
	SmartPet_Config.Alert = true; 
	SmartPet_AttackAlertFrame:Show(); 
	SmartPet_AddInfoMessage("You will now give an alert on attack"); 
else 
	PlaySound("igMainMenuOptionCheckBoxOn"); 
	SmartPet_Config.Alert = false; 
	SmartPet_AddInfoMessage("You will no longer give an alert on attack");
	SmartPet_AttackAlertFrame:Hide(); 
end

Scripts.OnMouseUp:
if ( this.isMoving ) then 
	this:StopMovingOrSizing(); 
	this.isMoving = false; 
end

Scripts.OnMouseDown:
if ( ( ( not this.isLocked ) or ( this.isLocked == 0 ) ) and ( arg1 == "LeftButton" ) ) then 
	this:StartMoving(); 
	this.isMoving = true; 
end

Scripts.OnHide:
SmartPet_Options_OnHide(); 
if ( this.isMoving ) then 
	this:StopMovingOrSizing(); 
	this.isMoving = false; 
end
