Scripts.OnReceiveDrag:
if ( not SprocketLowerScrollList.selectedIndex or not (SprocketLowerScrollList.selectedIndex == this:GetParent():GetID()) ) then
	this:GetParent():GetParent().selectedIndex = this:GetParent().itemIndex;
	this:GetParent():GetParent().selectedID = this:GetParent():GetID();
	SprocketConfig:CloseDewDrop();
	ScrollList_Update( this:GetParent():GetParent() );
end
SprocketTriggerItemConfigButton_ReceiveDrag( this:GetID() );

Scripts.OnClick:
if ( not SprocketLowerScrollList.selectedIndex or not (SprocketLowerScrollList.selectedIndex == this:GetParent():GetID()) ) then
	this:GetParent():GetParent().selectedIndex = this:GetParent().itemIndex;
	this:GetParent():GetParent().selectedID = this:GetParent():GetID();
	SprocketConfig:CloseDewDrop();
	ScrollList_Update( this:GetParent():GetParent() );
end
SprocketTriggerItemConfigButton_OnClick( this:GetID() );

Scripts.OnLoad:
this:SetID( this:GetParent():GetID() );
UIDropDownMenu_SetWidth(100);
getglobal(this:GetName().."Button"):SetScript( "OnClick", LowerMenuDropDown_OnClick );
getglobal(this:GetName().."Button"):SetID( this:GetParent():GetID() );
getglobal(this:GetName().."Button"):SetScript( "OnEnter", LowerMenuDropDown_OnEnter );
getglobal(this:GetName().."Button"):SetScript( "OnLeave", LowerMenuDropDown_OnLeave );

this.GetNumItems = SprocketTrigger_GetNumTriggers;
this.UpdateItem = SprocketTrigger_UpdateButton;
this.UpdateSelected = SprocketTrigger_UpdateSelected;
this.itemHeight = 30;
this.numDisplayItems = 3;

this.GetNumItems = SprocketLower_GetNumItems;
this.UpdateItem = SprocketLower_UpdateButton;
this.UpdateSelected = SprocketLower_UpdateSelected;
this.itemHeight = 75;
this.numDisplayItems = 3;

local x, y;

x = 230
y = -330;
local buttonAngle = 0;
local itemOffset = 60;
for index = 1, 8, 1 do
	curButton = getglobal( "SprocketItemConfigButton"..index );
	curButton:SetPoint( "CENTER", "SprocketMenuConfigFrame", "TOPLEFT", x + (itemOffset * cos(0-buttonAngle)), y + (itemOffset * sin(0-buttonAngle)) );
	buttonAngle = buttonAngle - 45;
end						
SprocketMenuConfigFrame_OnLoad()

Frames.Button:
/
this:RegisterForClicks( "LeftButtonUp" )
if ( SprocketConfigPopupFrame:IsShown() ) then SprocketConfigPopupFrame:Hide(); else if ( SprocketHintFrame:IsShown() ) then SprocketHintFrame:Hide() end SprocketConfigPopupFrame.mode = "centerbutton" SprocketConfigPopupFrame:Show() end
SprocketConfig:Set_Tooltip( CENTERICON, CENTERICON_BUTTON_TOOLTIP );
GameTooltip:Hide();

Scripts.OnClick:
if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOn");
else
	PlaySound("igMainMenuOptionCheckBoxOff");
end

SprocketConfig_MenuShowNames_OnClick();

