Scripts.OnUpdate:
if ( this.updatetext ) then
	if ( (this:GetName() == ( "TipBuddy_OptionsFrame_Slider2" )) 
	or (this:GetName() == ( "TipBuddy_OptionsFrame_Slider3" )) ) then
		this.updatetext:SetText( format("%.1f", ( this:GetValue() ) ) );
	elseif (this:GetName() == ( "TipBuddy_OptionsFrame_Slider6" )) then
		this.updatetext:SetText( format("%.2f", ( this:GetValue() ) ) );
	else
		this.updatetext:SetText( this:GetValue() );
	end
	return;
end
this.updatetext = getglobal( this:GetName().."TextUpdate");

Scripts.OnEnter:
if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end
if ( this.tooltipRequirement ) then
	GameTooltip:AddLine(this.tooltipRequirement, "", 1.0, 1.0, 1.0);
	GameTooltip:Show();
end

Scripts.OnUpdate:
if ( this.updatetext ) then
	--this.updatetext:SetText( format("%.1f", ( this:GetValue() ) ) );
	this.updatetext:SetText( this:GetValue() );
	return;
end
this.updatetext = getglobal( this:GetName().."TextUpdate");

Scripts.OnEnter:
getglobal(this:GetName().."SwatchBg"):SetVertexColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end
if ( this.tooltipRequirement ) then
	GameTooltip:AddLine(this.tooltipRequirement, "", 1.0, 1.0, 1.0);
	GameTooltip:Show();
end

getglobal(this:GetName().."SwatchBg"):SetVertexColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end
if ( this.tooltipRequirement ) then
	GameTooltip:AddLine(this.tooltipRequirement, "", 1.0, 1.0, 1.0);
	GameTooltip:Show();
end

TipBuddy_Header_Frame:SetAlpha( 0.9 );
GameTooltip_SetDefaultAnchor(GameTooltip, this);
GameTooltip:SetText(TB_TIPBUDDYANCHORCLOSE_TOOLTIP, nil, nil, nil, nil, 1);

TipBuddy.anchor, TipBuddy.fanchor, TipBuddy.offset = TipBuddy_GetFrameAnchorPos();
if (TipBuddy_SavedVars["general"].anchored == 1) then
	TipBuddy_Parent_Frame:SetPoint(TipBuddy.anchor, "TipBuddy_Header_Frame", TipBuddy.fanchor, 0, 0);
end
this:SetAlpha( 0.9 );
GameTooltip_SetDefaultAnchor(GameTooltip, TipBuddy_Parent_Frame);
GameTooltip:SetText(TB_TIPBUDDYANCHOR_TOOLTIP, nil, nil, nil, nil, 1);
TipBuddy_SavedVars["general"].framepos_L = TipBuddy_Header_Frame:GetLeft();
TipBuddy_SavedVars["general"].framepos_T = TipBuddy_Header_Frame:GetTop();

Scripts.OnUpdate:
if (GameTooltip:IsVisible() or TipBuddyTooltip:IsVisible() or TipBuddy_Main_Frame:IsVisible()) then
	TipBuddy_ParentTip_OnUpdate();
end

Scripts.OnLoad:
this.unit = "mouseover";
this:RegisterEvent("UNIT_MANA");
this:RegisterEvent("UNIT_RAGE");
this:RegisterEvent("UNIT_FOCUS");
this:RegisterEvent("UNIT_ENERGY");
this:RegisterEvent("UNIT_HAPPINESS");
this:RegisterEvent("UNIT_MAXMANA");
this:RegisterEvent("UNIT_MAXRAGE");
this:RegisterEvent("UNIT_MAXFOCUS");
this:RegisterEvent("UNIT_MAXENERGY");
this:RegisterEvent("UNIT_MAXHAPPINESS");
this:RegisterEvent("UNIT_DISPLAYPOWER");
TextStatusBar_Initialize();
this.textLockable = 1;

Scripts.OnShow:
if ( TipBuddyTooltipStatusBar:IsVisible() ) then
	this:ClearAllPoints();
	this:SetPoint( "TOPLEFT", "TipBuddyTooltipStatusBar", "BOTTOMLEFT", 0, -1);
else
	this:ClearAllPoints();
	this:SetPoint( "TOPLEFT", "TipBuddyTooltip", "BOTTOMLEFT", 4, -1);
end

Scripts.OnKeyDown:
if ( arg1 == "ESCAPE" ) then
	TipBuddy_ToggleOptionsFrame();
elseif ( arg1 == "ENTER") then
	ChatFrame_OpenChat("", chatFrame);
elseif ( arg1 == "/") then
	ChatFrame_OpenChat("/", chatFrame);
elseif ( arg1 == strupper(GetBindingKey("SCREENSHOT"))) then
	RunBinding("SCREENSHOT");
end

Scripts.OnShow:
 				if (MYADDONS_ACTIVE_OPTIONSFRAME == this) then
					HideUIPanel(GameMenuFrame);
					HideUIPanel(myAddOnsFrame);
					TipBuddy_OptionsFrame_UpdateCheckboxes();
					TipBuddy_OptionsFrame_UpdateSliders();
					TipBuddy_OptionsFrame_UpdateColorButtons();
					TipBuddy_OptionsFrame_UpdateColorButtons_Text();

					TipBuddy_Background_ColorPick_OnLoad();

					PlaySound("igMainMenuQuit");
				end
				if (not TipBuddy_SavedVars["general"].opPos_L) then
					return;
				else
					this:SetPoint("TOPLEFT", "UIParent", "BOTTOMLEFT", TipBuddy_SavedVars["general"].opPos_L, TipBuddy_SavedVars["general"].opPos_T);
				end

Scripts.OnClick:
if ( this:GetChecked() ) then TipBuddy_Header_Frame:Show();
else
end

if (TB_TextVariablesFrame:IsVisible()) then
	TB_TextVariablesFrame:Hide();
	TipBuddy_ShowTextVars:SetText("Show Vars");
else
	TB_TextVariablesFrame:Show();
	TipBuddy_ShowTextVars:SetText("Hide Vars");
end

Scripts.OnKeyDown:
if ( arg1 == "ESCAPE" ) then
	this:Hide();
	TipBuddy_ToggleOptionsFrame();
elseif ( arg1 == "ENTER") then
	ChatFrame_OpenChat("", chatFrame);
elseif ( arg1 == "/") then
	ChatFrame_OpenChat("/", chatFrame);
elseif ( arg1 == strupper(GetBindingKey("SCREENSHOT"))) then
	RunBinding("SCREENSHOT");
end

Scripts.OnEnter:

TipBuddy.unitframe = 1;
--TB_AddMessage("entering unit frame: "..TargetFrame.unit);
TipBuddy.targetType = TipBuddy_TargetInfo_GetTargetType( TargetFrame.unit );

GameTooltip_SetDefaultAnchor(GameTooltip, UIParent);
GameTooltip:SetUnit(TargetFrame.unit);
TipBuddy_SetFrame_Visibility( TipBuddy.targetType, TargetFrame.unit );
-- hiding health and mana bars because they won't update
if ( TipBuddy_SavedVars[TipBuddy.targetType].off ~= 0 ) then
	TipBuddy_TargetFrameHealthBar:Hide();
	TipBuddy_TargetFrameManaBar:Hide();
	-- setting frame size again
	TipBuddy_FrameHeights_Initialize(TipBuddy.targetType);
	TipBuddy_SetFrame_Width();
end

TipBuddy_ShowRank( TipBuddy.targetType, TargetFrame.unit );
TipBuddy_SetFrame_BackgroundColor( TipBuddy.targetType, TargetFrame.unit );
TipBuddy_TargetBuffs_Update( TipBuddy.targetType, TargetFrame.unit );

Scripts.OnClick:
HideUIPanel(this:GetParent());
TBColorPickerFrame.func();
if ( TBColorPickerFrame.opacityFunc ) then
	TBColorPickerFrame.opacityFunc();
end
TipBuddy_Background_SetColor();
TipBuddy_OptionsFrame_UpdateColorButtons();
TipBuddy_InitializeTextColors();

Scripts.OnUpdate:
if ( this.updatetext ) then

	this.updatetext:SetText( "Alpha:\n"..(format("%.0f", ( ((this:GetValue() - 1)*-100) ) )).."%" ); 
	return;
end
this.updatetext = getglobal( this:GetName().."TextUpdate");

Scripts.OnShow:
this:Raise();
if ( this.hasOpacity ) then
	TBOpacitySliderFrame:Show();
	TBOpacitySliderFrame:SetValue(this.opacity);
	this:SetWidth(365);
else
	TBOpacitySliderFrame:Hide();
	this:SetWidth(305);
end
PlaySound("gsTitleOptionOK");

Scripts.OnColorSelect:
TBColorSwatch:SetTexture(arg1, arg2, arg3);
if ( this.func ) then
	this.func();
end

Scripts.OnKeyDown:
if (arg1 == "ESCAPE") then
	HideUIPanel(this);
	if ( TBColorPickerFrame.cancelFunc ) then
		TBColorPickerFrame.cancelFunc(TBColorPickerFrame.previousValues);
	end
end

Scripts.OnClick:
HideUIPanel(this:GetParent());
TBColorPickerFrame_Text.func();
if ( TBColorPickerFrame_Text.opacityFunc ) then
	TBColorPickerFrame_Text.opacityFunc();
end
TipBuddy_Background_SetColor_Text();
TipBuddy_OptionsFrame_UpdateColorButtons_Text();
TipBuddy_InitializeTextColors();

