	TBOpacitySliderFrame_Text:SetValue(this.opacity);
	this:SetWidth(365);
else
	TBOpacitySliderFrame_Text:Hide();
	this:SetWidth(305);
end
PlaySound("gsTitleOptionOK");

Scripts.OnColorSelect:
