Scripts.OnEvent:
if (event == "VARIABLES_LOADED") then
	Uber_Init();
	UberHealConfigure();
elseif (event == "SPELLCAST_FAILED") then
	targetOutOfLOS();
elseif (event == "SPELLCAST_STOP") then
	UberSpellCastStop();
end

if (event == "VARIABLES_LOADED") then
	UberHealOption_OnShow();
	UberHealConfigure();
elseif (event == "SPELLCAST_FAILED") then
	targetOutOfLOS();
end
