Scripts.OnShow:
this:SetTextColor(0,0,0);
dummyFrame = getglobal(this:GetName() .. "DummyFrame");

if dummyFrame ~= nil then
  dummyFrame:SetAlpha(0.05);
end

Scripts.OnEnter:
dummyFrame = getglobal(this:GetName() .. "DummyFrame");

if dummyFrame ~= nil then
  dummyFrame:SetAlpha(0.2);
end

Scripts.OnLeave:
dummyFrame = getglobal(this:GetName() .. "DummyFrame");

if dummyFrame ~= nil then
  dummyFrame:SetAlpha(0.05);
end

Scripts.OnClick:
	    local test = getglobal(this:GetName() .. "_OnClick");

	    if test ~= nil then
	        FlagRSP.printDebug("Calling " .. this:GetName() .. "_OnClick");
     	        test();
	    else
	        FlagRSP.printDebug("Calling " .. this:GetParent():GetName() .. ":Hide");
	        --test = getglobal(this:GetParent():GetName() .. ":Hide");
	        --test();
                this:GetParent():Hide();
	    end

Scripts.OnTextChanged:
scrollFrame = getglobal(string.sub(this:GetName(),1,string.len(this:GetName())-17));

ScrollingEdit_OnTextChanged(scrollFrame);

update = getglobal(this:GetName() .. "_UpdateDescBox");
if update ~= nil then
--FriendlistCharFrame_UpdateDescBox();
  update();
else
  flagRSPCharPageMultilineEditBox_Update();
end

Scripts.OnCursorChanged:
ScrollingEdit_OnCursorChanged(arg1, arg2-10, arg3, arg4);

update = getglobal(this:GetName() .. "_UpdateDescBox");
if update ~= nil then
--FriendlistCharFrame_UpdateDescBox();
  update();
else
  flagRSPCharPageMultilineEditBox_Update();
end

Scripts.OnUpdate:
scrollFrame = getglobal(string.sub(this:GetName(),1,string.len(this:GetName())-17));
ScrollingEdit_OnUpdate(scrollFrame);

update = getglobal(this:GetName() .. "_UpdateDescBox");
if update ~= nil then
  update();
else
  flagRSPCharPageMultilineEditBox_Update();
end

Scripts.OnShow:
text = getglobal(this:GetName() .. "Text");
this:SetTextColor(0,0,0);

dummyFrame = getglobal(string.sub(this:GetName(),1,string.len(this:GetName())-17) .. "DummyFrame");

if dummyFrame ~= nil then
  dummyFrame:SetAlpha(0.05);
end

Scripts.OnEnter:
dummyFrame = getglobal(string.sub(this:GetName(),1,string.len(this:GetName())-17) .. "DummyFrame");

if dummyFrame ~= nil then
  dummyFrame:SetAlpha(0.2);
end

Scripts.OnLeave:
dummyFrame = getglobal(string.sub(this:GetName(),1,string.len(this:GetName())-17) .. "DummyFrame");

if dummyFrame ~= nil then
  dummyFrame:SetAlpha(0.05);
end

Scripts.OnEditFocusGained:
update = getglobal(this:GetName() .. "_OnDescBoxFocus");
if update ~= nil then
  update();
else
  flagRSPCharPageMultilineEditBox_Update();
end
--FriendlistCharFrame_OnDescBoxFocus();

Scripts.OnClick:
if (FRIENDLISTFrame.selectedMember > 0) then
if FRIENDLISTStaticPopUpEdit:IsVisible() then
FRIENDLISTStaticPopUpEdit:Hide();
end
FRIENDLISTStaticPopUpEdit:Show();
else
--Print_Chat("Keine Person/Gilde zum editieren ausgewählt");
end
