Scripts.OnMouseDown:
if (arg1 == "LeftButton") then
m_options:StartMoving();
elseif (arg2 == "RightButton") then
m_options:Hide();
end

Scripts.OnValueChanged:
if(m_boptions) then
m_boptions.scale = arg1;
local tmppercent = floor(arg1 * 100);
m_optionsPanel5Panel1Component31Label:SetText(tmppercent);
m_BuffFrame:SetScale(arg1);
m_BuffFrameCapsule:SetScale(arg1);
end

if(m_doptions) then
m_doptions.scale = arg1;
local tmppercent = floor(arg1 * 100);
m_optionsPanel5Panel1Component35Label:SetText(tmppercent);
m_DeBuffFrame:SetScale(arg1);
m_DeBuffFrameCapsule:SetScale(arg1);
end

if(m_woptions) then
m_woptions.scale = arg1;
local tmppercent = floor(arg1 * 100);
m_optionsPanel5Panel1Component39Label:SetText(tmppercent);
m_WepBuffFrame:SetScale(arg1);
m_WepBuffFrameCapsule:SetScale(arg1);
end
